ON [dbo].[LotteryResults]
WITH EXECUTE AS CALLER
FOR INSERT
AS
BEGIN
		INSERT INTO [dbo].[Vouchers]
			([OrderId],[Code])
		VALUES
			(
				(SELECT 
					[Orders].[OrderId] 
				FROM 
					Orders INNER JOIN OrderNumbers ON
						Orders.OrderId = OrderNumbers.OrderId INNER JOIN inserted ON
					[Orders].[DrawDate] = [inserted].[DrawDate]
				WHERE
					Orders.DrawDate = inserted.[DrawDate] AND [OrderNumbers].[Number] = [inserted].[Number]),
				SUBSTRING(CONVERT(varchar(255), NEWID()), 0, 9)
			)
	END
GO


